    {
        "name": "Insurance.get_investment_return",
        "description": "获取特定保险类型的投资回报率。",
        "required_parameters": [
            {
                "name": "insurance_type",
                "type": "STRING",
                "description": "保险类型"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "return_rate",
                "description": "投资回报率"
            }
        ]
    },
    {
        "name": "Loan.calculate_amortization_schedule",
