            {
                "name": "loan_amount",
                "type": "NUMBER",
                "description": "贷款总额"
            },
            {
                "name": "interest_rate",
                "type": "NUMBER",
                "description": "年利率"
            },
            {
                "name": "loan_term",
                "type": "NUMBER",
                "description": "贷款期限（以月为单位）"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "amortization_schedule",
                "description": "分期偿还计划"
            }
        ]
    },
    {
        "name": "Stock.get_historical_prices",
        "description": "获取某只股票的历史价格数据。",
        "required_parameters": [
            {
                "name": "stock_symbol",
                "type": "STRING",
                "description": "股票代码"
            },
            {
                "name": "start_date",
                "type": "STRING",
                "description": "开始日期（格式：YYYY-MM-DD）"
            },
            {
                "name": "end_date",
                "type": "STRING",
                "description": "结束日期（格式：YYYY-MM-DD）"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "historical_prices",
                "description": "历史价格数据"
            }
        ]
