        "required_parameters": [
            {
                "name": "risk_tolerance",
                "type": "STRING",
                "description": "风险承受能力（如：低、中、高）"
            },
            {
                "name": "comparison_result",
                "type": "STRING",
                "description": "投资回报率的比较结果"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "recommended_insurance_type",
