        "optional_parameters": [],
        "return_data": [
            {
                "name": "description",
                "description": "理赔处理流程的详细描述"
            }
        ]
    },
    {
        "name": "Financial.exchange_rate_forecast",
        "description": "预测指定货币对的汇率",
        "required_parameters": [
            {
                "name": "currency_pair",
                "type": "STRING",
                "description": "货币对名称（如USD/CNY）"
