        "name": "Insurance.environmental_social_performance",
        "description": "获取多家保险公司在环境和社会责任方面的表现对比",
        "required_parameters": [
            {
                "name": "company_names",
                "type": "LIST",
                "description": "需要对比的保险公司名称列表"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "performance_comparison",
                "description": "各保险公司在环境和社会责任方面的表现对比数据"
            }
        ]
    },
    {
        "name": "Insurance.sustainable_development_concept",
        "description": "获取保险行业可持续发展的概念和定义",
        "required_parameters": [],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "concept",
                "description": "保险行业可持续发展的概念和定义"
            }
        ]
    },
    {
        "name": "Insurance.company_case_study",
        "description": "获取特定保险公司的案例研究",
        "required_parameters": [
            {
                "name": "company_name",
                "type": "STRING",
                "description": "需要获取案例研究的保险公司名称"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "case_study",
                "description": "特定保险公司的案例研究内容"
            }
        ]
    },
    {
        "name": "Insurance.recommendation",
        "description": "根据提供的保险信息生成推荐建议",
        "required_parameters": [
            {
                "name": "information",
                "type": "STRING",
                "description": "用户提供的保险相关信息"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "recommendation",
                "description": "根据用户提供的信息生成的保险推荐建议"
            }
        ]
    },
    {
        "name": "Insurance.market_trend_analysis",
        "description": "分析保险市场的最新趋势和发展方向",
        "required_parameters": [],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "trend_analysis",
                "description": "保险市场的最新趋势和发展方向分析报告"
            }
        ]
    },
    {
        "name": "Insurance.product_comparison",
        "description": "比较不同保险公司提供的类似保险产品的优劣",
        "required_parameters": [
            {
                "name": "product_names",
                "type": "LIST",
                "description": "需要比较的保险产品名称列表"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "comparison_result",
                "description": "不同保险公司提供的类似保险产品的优劣比较结果"
            }
        ]
    },
    {
        "name": "Insurance.customer_satisfaction_survey",
        "description": "获取特定保险公司客户的满意度调查结果",
        "required_parameters": [
            {
                "name": "company_name",
                "type": "STRING",
                "description": "需要获取客户满意度调查结果的保险公司名称"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "satisfaction_survey",
                "description": "特定保险公司客户的满意度调查结果"
