        "name": "MarketData.fetch",
        "description": "获取指定公司的市场数据，包括市场表现和海外记录。",
        "required_parameters": [
            {
                "name": "company_id",
                "description": "公司的唯一标识符。",
                "type": "STRING"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "market_data",
                "description": "公司的市场数据。"
            },
            {
                "name": "overseas_records",
                "description": "公司的海外市场记录。"
            }
        ]
    },
    {
        "name": "FinancialReports.fetch",
        "description": "获取指定公司的财务报告，包括季度和年度报告。",
        "required_parameters": [
            {
                "name": "company_id",
                "description": "公司的唯一标识符。",
                "type": "STRING"
            }
