    {
        "name": "Strategy.analyze",
        "description": "分析市场数据和合作伙伴信息，生成战略建议。",
        "required_parameters": [
            {
                "name": "market_data",
                "description": "市场数据对象。",
                "type": "OBJECT"
            },
            {
                "name": "partners",
                "description": "合作伙伴列表。",
                "type": "LIST"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "strategic_recommendations",
                "description": "战略建议。"
            }
        ]
    },
    {
        "name": "RiskAssessment.calculate",
        "description": "计算指定公司的风险评估，包括市场风险和信用风险。",
        "required_parameters": [
            {
                "name": "company_id",
                "description": "公司的唯一标识符。",
                "type": "STRING"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "risk_assessment",
                "description": "公司的风险评估结果。"
            }
        ]
    },
    {
        "name": "Portfolio.optimize",
        "description": "优化投资组合，基于市场数据和风险偏好。",
        "required_parameters": [
            {
                "name": "market_data",
                "description": "市场数据对象。",
                "type": "OBJECT"
            },
            {
                "name": "risk_preference",
                "description": "风险偏好，如'low', 'medium', 'high'。",
                "type": "STRING"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "optimized_portfolio",
                "description": "优化后的投资组合。"
            }
        ]
    },
    {
        "name": "PartnerData.fetch",
        "description": "获取指定公司的合作伙伴信息。",
        "required_parameters": [
            {
                "name": "company_id",
                "description": "公司的唯一标识符。",
                "type": "STRING"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "partners",
                "description": "公司的合作伙伴信息。"
            }
        ]
    },
    {
        "name": "HistoricalData.fetch",
        "description": "获取指定公司的历史数据，包括股价和交易量。",
        "required_parameters": [
            {
                "name": "company_id",
                "description": "公司的唯一标识符。",
                "type": "STRING"
            }
        ],
        "optional_parameters": [
            {
                "name": "time_period",
                "description": "时间周期，如'1M', '6M', '1Y'。",
                "type": "STRING"
            }
        ],
        "return_data": [
            {
                "name": "historical_data",
                "description": "公司的历史数据。"
            }
        ]
