    {
        "name": "FinancialData.predict_future_trends",
        "description": "基于历史数据和市场情绪，预测特定股票或市场的未来趋势。",
        "required_parameters": [
            {
                "name": "ticker",
                "type": "STRING",
                "description": "股票或市场的代码。"
            },
            {
                "name": "historical_data",
                "type": "STRING",
                "description": "历史价格和交易量数据。"
            },
            {
                "name": "sentiment_data",
                "type": "STRING",
                "description": "市场情绪数据。"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "future_trends",
                "description": "返回的未来趋势预测数据。"
            }
        ]
    }
