            {
                "name": "product_name",
                "type": "STRING",
                "description": "保险产品的名称。"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "coverage",
                "description": "保险产品的覆盖范围。"
            },
            {
                "name": "cost",
                "description": "保险产品的成本。"
            }
        ]
    },
    {
        "name": "BusinessInsurance.get_business_info",
        "description": "获取企业的基本信息，包括业务类型和风险信息。",
        "required_parameters": [],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "business_type",
                "description": "企业的业务类型。"
            },
            {
                "name": "risk_info",
                "description": "企业的风险信息。"
            }
        ]
    },
    {
        "name": "BusinessInsurance.provide_selection_advice",
        "description": "根据企业的业务类型、风险信息和推荐的保险产品，提供选择建议。",
        "required_parameters": [
            {
                "name": "business_type",
                "type": "STRING",
                "description": "企业的业务类型。"
