            {
                "name": "advice",
                "description": "选择保险产品的建议。"
            }
        ]
    },
    {
        "name": "BusinessInsurance.recommend_insurance_products",
        "description": "根据企业的业务类型和风险信息，推荐合适的保险产品。",
        "required_parameters": [
            {
                "name": "business_type",
                "type": "STRING",
