            {
                "name": "policy_number",
                "type": "STRING",
                "description": "保单号码。"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "status",
                "description": "保单的当前状态。"
            },
            {
                "name": "expiration_date",
                "description": "保单的到期日期。"
            }
        ]
    },
    {
        "name": "BusinessInsurance.calculate_premium",
