        "name": "Insurance.operational_risk_management_recommendation",
        "description": "获取特定公司运营风险管理的推荐策略",
        "required_parameters": [
            {
                "name": "company_names",
                "type": "LIST",
                "description": "需要获取推荐策略的公司名称列表"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "recommendations",
                "description": "返回的推荐策略列表"
            }
        ]
    },
    {
        "name": "Insurance.operational_risk_management_intro",
        "description": "获取运营风险管理的介绍信息",
        "required_parameters": [],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "introduction",
                "description": "返回的运营风险管理介绍信息"
            }
        ]
    },
    {
        "name": "Insurance.operational_risk_management_case_study",
        "description": "获取特定公司运营风险管理的案例研究",
        "required_parameters": [
            {
                "name": "company_names",
                "type": "LIST",
                "description": "需要获取案例研究的公司名称列表"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "case_studies",
                "description": "返回的案例研究列表"
            }
        ]
    },
    {
        "name": "Insurance.operational_risk_service_comparison",
        "description": "获取特定公司运营风险服务之间的比较",
        "required_parameters": [
            {
                "name": "company_names",
                "type": "LIST",
                "description": "需要进行比较的公司名称列表"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "comparison",
                "description": "返回的比较结果"
            }
        ]
    },
    {
        "name": "Insurance.operational_risk_management_trend_analysis",
        "description": "获取运营风险管理趋势分析",
        "required_parameters": [
            {
                "name": "time_period",
                "type": "STRING",
                "description": "需要分析的时间段（例如：'2020-2023'）"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "trend_analysis",
                "description": "返回的趋势分析结果"
            }
