        "required_parameters": [],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "best_practices",
                "description": "返回的最佳实践列表"
            }
        ]
    },
    {
        "name": "Insurance.operational_risk_management_tool_evaluation",
        "description": "获取运营风险管理工具的评估报告",
        "required_parameters": [
            {
                "name": "tool_names",
                "type": "LIST",
                "description": "需要评估的工具名称列表"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "evaluation_report",
                "description": "返回的评估报告"
            }
