            {
                "name": "interest_rate",
                "type": "FLOAT",
                "description": "贷款的年利率。"
            },
            {
                "name": "loan_term",
                "type": "INTEGER",
                "description": "贷款的期限（以月为单位）。"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "repayment_schedule",
                "description": "贷款的还款计划，包括每月还款金额和总利息。"
            }
        ]
    },
    {
        "name": "Insurance.get_coverage",
        "description": "获取特定保险计划的覆盖范围详细信息。",
