        ]
    },
    {
        "name": "TechInnovation.get_tech_conversion_rate",
        "description": "获取指定公司的技术转化率，即研发成果转化为实际产品的比例。",
        "required_parameters": [
            {
                "name": "company_name",
                "type": "STRING",
                "description": "公司名称"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "conversion_rate",
                "description": "技术转化率，范围为0到1。"
            }
        ]
    },
    {
        "name": "TechInnovation.compare_companies",
        "description": "比较两家公司在研发投资和专利数量上的差异。",
        "required_parameters": [
            {
                "name": "company_name_1",
                "type": "STRING",
                "description": "第一家公司的名称"
            },
            {
                "name": "company_name_2",
                "type": "STRING",
                "description": "第二家公司的名称"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "RnD_investment_difference",
                "description": "两家公司在研发投资上的差异，单位为美元。"
            },
            {
                "name": "patents_difference",
                "description": "两家公司在专利数量上的差异。"
            }
        ]
