        "name": "TechInnovation.predict_future_innovation",
        "description": "基于历史数据预测公司未来的创新能力。",
        "required_parameters": [
            {
                "name": "company_name",
                "type": "STRING",
                "description": "公司名称"
            },
            {
                "name": "historical_data",
                "type": "LIST",
                "description": "公司过去几年的研发投资和专利数量数据。"
            }
