        "name": "Insurance.service_quality_comparison",
        "description": "比较不同保险公司服务质量的API",
        "required_parameters": [
            {
                "name": "company_names",
                "type": "LIST",
                "description": "需要比较的保险公司名称列表"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "comparison",
                "description": "服务质量比较结果"
            }
        ]
    },
    {
        "name": "Insurance.customized_service_intro",
        "description": "获取保险公司定制服务介绍的API",
        "required_parameters": [],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "description",
                "description": "定制服务介绍内容"
            }
        ]
    },
    {
        "name": "Insurance.case_study",
        "description": "获取特定保险公司的案例研究的API",
        "required_parameters": [
            {
                "name": "company_name",
                "type": "STRING",
                "description": "需要获取案例研究的保险公司名称"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "case_study",
                "description": "案例研究内容"
            }
        ]
    },
    {
        "name": "Insurance.recommendation",
        "description": "获取保险公司推荐信息的API",
        "required_parameters": [],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "recommendation",
                "description": "推荐信息内容"
            }
        ]
    },
    {
        "name": "Insurance.premium_calculator",
        "description": "计算保险费用的API",
        "required_parameters": [
            {
                "name": "policy_type",
                "type": "STRING",
                "description": "保险政策类型"
            },
            {
                "name": "coverage_amount",
                "type": "FLOAT",
                "description": "保险覆盖金额"
            }
        ],
        "optional_parameters": [
            {
                "name": "age",
                "type": "INTEGER",
                "description": "被保险人年龄"
            }
        ],
        "return_data": [
            {
                "name": "premium",
                "description": "计算出的保险费用"
            }
        ]
    },
    {
        "name": "Insurance.claim_status_check",
        "description": "查询保险理赔状态的API",
