            {
                "name": "claim_id",
                "type": "STRING",
                "description": "理赔ID"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "status",
                "description": "理赔状态"
            }
        ]
    },
    {
        "name": "Insurance.policy_renewal_options",
        "description": "获取保险政策续保选项的API",
        "required_parameters": [
            {
                "name": "policy_id",
                "type": "STRING",
                "description": "保险政策ID"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "renewal_options",
                "description": "续保选项列表"
