    {
        "name": "Insurance.risk_assessment",
        "description": "根据用户的风险偏好和财务状况，评估潜在的保险风险。",
        "required_parameters": ["risk_tolerance", "financial_status"],
        "optional_parameters": ["additional_info"],
        "return_data": [
            {
                "name": "risk_level",
                "description": "返回评估出的风险等级。"
            }
        ]
    },
    {
        "name": "Insurance.policy_renewal_reminder",
        "description": "提醒用户保险政策的续保日期，避免错过续保时间。",
        "required_parameters": ["policy_id"],
        "optional_parameters": ["email_notification"],
        "return_data": [
            {
                "name": "renewal_date",
                "description": "返回保险政策的续保日期。"
            }
        ]
    }
