    {
        "name": "BusinessInfo.get",
        "description": "获取指定公司的基本业务信息",
        "required_parameters": [
            {
                "name": "company_id",
                "description": "公司ID",
                "type": "STRING"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "business_info",
                "description": "业务信息"
            }
        ]
    },
    {
        "name": "MarketTrend.predict",
        "description": "预测特定市场的未来趋势",
        "required_parameters": [
            {
                "name": "market_id",
                "description": "市场ID",
                "type": "STRING"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "market_trend_prediction",
                "description": "市场趋势预测结果"
            }
        ]
    },
    {
        "name": "ApprovalProcess.analyze",
        "description": "分析指定公司的审批流程",
        "required_parameters": [
            {
                "name": "business_info",
                "description": "业务信息",
                "type": "DICT"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "approval_process_analysis",
                "description": "审批流程分析结果"
            }
        ]
    },
    {
        "name": "InvestmentOpportunity.list",
        "description": "列出当前可用的投资机会",
        "required_parameters": [],
        "optional_parameters": [
            {
                "name": "risk_level",
                "description": "风险等级",
                "type": "STRING"
            }
        ],
        "return_data": [
            {
                "name": "investment_opportunities",
                "description": "投资机会列表"
            }
        ]
    },
    {
        "name": "ApprovalProcess.suggest",
        "description": "根据业务信息和财务记录建议审批流程",
        "required_parameters": [
            {
                "name": "business_info",
                "description": "业务信息",
                "type": "DICT"
            },
            {
                "name": "finance_records",
                "description": "财务记录列表",
                "type": "LIST"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "approval_process_suggestion",
                "description": "审批流程建议"
