        "name": "BankData.generate_aml_report",
        "description": "生成反洗钱报告",
        "required_parameters": [
            {
                "name": "kyc_info",
                "type": "LIST",
                "description": "客户KYC信息列表"
            },
            {
                "name": "high_risk_transactions",
                "type": "LIST",
                "description": "高风险交易记录列表"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "report",
                "description": "生成的反洗钱报告"
            }
        ]
    },
    {
        "name": "BankData.get_customer_kyc_info",
        "description": "获取客户KYC信息",
        "required_parameters": [
            {
                "name": "customer_ids",
                "type": "LIST",
                "description": "客户ID列表"
