            }
        ]
    },
    {
        "name": "BankData.filter_high_risk_transactions",
        "description": "过滤高风险交易记录",
        "required_parameters": [
            {
                "name": "records",
                "type": "LIST",
                "description": "交易记录列表"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "high_risk_transactions",
                "description": "高风险交易记录"
            }
        ]
    },
    {
        "name": "BankData.get_transaction_records",
        "description": "获取交易记录",
        "required_parameters": [
            {
                "name": "query",
                "type": "STRING",
                "description": "查询条件"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "records",
                "description": "交易记录"
            }
        ]
    },
    {
        "name": "BankData.analyze_transaction_patterns",
