            {
                "name": "transaction_records",
                "type": "LIST",
                "description": "交易记录列表"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "patterns",
                "description": "交易模式分析结果"
            }
        ]
    },
    {
        "name": "BankData.calculate_customer_risk_score",
        "description": "计算客户风险评分",
        "required_parameters": [
            {
                "name": "kyc_info",
                "type": "LIST",
                "description": "客户KYC信息列表"
            },
            {
                "name": "transaction_records",
                "type": "LIST",
                "description": "交易记录列表"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "risk_score",
                "description": "客户风险评分"
            }
        ]
    },
    {
        "name": "BankData.monitor_suspicious_activities",
        "description": "监控可疑活动",
        "required_parameters": [
            {
                "name": "transaction_records",
                "type": "LIST",
                "description": "交易记录列表"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "suspicious_activities",
                "description": "可疑活动列表"
            }
        ]
    }
