        "name": "BankCompliance.get_transaction_history",
        "description": "获取客户的交易历史记录",
        "required_parameters": [
            {
                "name": "customer_ids",
                "type": "LIST",
                "description": "客户ID列表"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "transaction_history",
                "description": "交易历史记录"
            }
        ]
    },
    {
        "name": "BankCompliance.generate_report",
        "description": "生成合规报告",
        "required_parameters": [
            {
                "name": "late_updates",
                "type": "LIST",
                "description": "延迟更新的列表"
            }
        ],
        "optional_parameters": [],
