        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "aml_status",
                "description": "反洗钱状态"
            }
        ]
    },
    {
        "name": "BankCompliance.update_customer_profile",
        "description": "更新客户KYC信息",
        "required_parameters": [
            {
                "name": "customer_id",
                "type": "STRING",
                "description": "客户ID"
            },
            {
                "name": "kyc_data",
                "type": "DICT",
                "description": "KYC数据"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "update_status",
                "description": "更新状态"
            }
        ]
    },
    {
        "name": "BankCompliance.get_suspicious_activity",
        "description": "获取可疑交易活动",
        "required_parameters": [
            {
                "name": "time_period",
                "type": "STRING",
                "description": "时间周期"
