        "name": "ComplianceAnalysis.provide_recommendations",
        "description": "根据提供的法律记录和行业标准，生成合规建议。",
        "required_parameters": [
            {
                "name": "legal_records",
                "type": "OBJECT",
                "description": "法律合规记录，包含公司或组织的法律合规历史数据。"
            },
            {
                "name": "industry_standards",
                "type": "OBJECT",
                "description": "行业标准，包含当前行业内的合规要求和标准。"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "compliance_recommendations",
                "description": "生成的合规建议，帮助公司或组织满足法律和行业标准。"
            }
        ]
    },
    {
        "name": "FinancialReporting.generate_report",
        "description": "生成财务报告，包括收入、支出、利润等关键财务指标。",
        "required_parameters": [
            {
                "name": "financial_data",
                "type": "OBJECT",
                "description": "财务数据，包含收入、支出、利润等关键财务指标。"
            }
        ],
        "optional_parameters": [
            {
                "name": "report_format",
                "type": "STRING",
                "description": "报告格式，可选值包括PDF、CSV、JSON等。"
            }
        ],
        "return_data": [
            {
                "name": "financial_report",
                "description": "生成的财务报告，包含详细的财务数据和分析。"
            }
        ]
    },
    {
        "name": "RiskManagement.assess_risk",
        "description": "评估公司或组织的风险水平，包括市场风险、信用风险等。",
        "required_parameters": [
            {
                "name": "financial_data",
                "type": "OBJECT",
                "description": "财务数据，包含收入、支出、利润等关键财务指标。"
            },
            {
                "name": "market_data",
                "type": "OBJECT",
                "description": "市场数据，包含市场趋势、竞争对手分析等。"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "risk_assessment",
                "description": "风险评估结果，包含市场风险、信用风险等评估指标。"
            }
        ]
    },
    {
        "name": "LegalCompliance.get_records",
        "description": "获取公司或组织的法律合规记录。",
        "required_parameters": [],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "compliance_records",
                "description": "法律合规记录，包含公司或组织的法律合规历史数据。"
            }
        ]
    },
    {
        "name": "PortfolioManagement.optimize_portfolio",
        "description": "优化投资组合，根据市场数据和风险偏好调整资产配置。",
        "required_parameters": [
            {
                "name": "portfolio_data",
                "type": "OBJECT",
                "description": "投资组合数据，包含当前资产配置和市场数据。"
            },
            {
                "name": "risk_preference",
                "type": "STRING",
                "description": "风险偏好，可选值包括保守、平衡、激进等。"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "optimized_portfolio",
                "description": "优化后的投资组合，包含调整后的资产配置和预期收益。"
            }
        ]
    },
    {
        "name": "IndustryCompliance.get_standards",
        "description": "获取当前行业内的合规标准和要求。",
        "required_parameters": [],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "industry_standards",
                "description": "行业标准，包含当前行业内的合规要求和标准。"
            }
        ]
    },
    {
        "name": "TaxPlanning.calculate_taxes",
        "description": "计算公司或组织的税务负担，并提供税务规划建议。",
        "required_parameters": [
            {
                "name": "financial_data",
                "type": "OBJECT",
                "description": "财务数据，包含收入、支出、利润等关键财务指标。"
