    {
        "name": "MarketTrends.get_sector_performance",
        "description": "获取特定行业的表现数据。",
        "required_parameters": [
            {
                "name": "sector_name",
                "type": "STRING",
                "description": "行业名称。"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "sector_performance",
                "description": "行业表现数据。"
            }
        ]
