        "name": "PortfolioManagement.optimize_portfolio",
        "description": "优化投资组合，以最大化回报或最小化风险。",
        "required_parameters": [
            {
                "name": "portfolio_data",
                "type": "OBJECT",
                "description": "投资组合数据。"
            }
        ],
        "optional_parameters": [
            {
                "name": "risk_tolerance",
                "type": "FLOAT",
                "description": "风险承受能力（可选）。"
            }
        ],
        "return_data": [
            {
                "name": "optimized_portfolio",
                "description": "优化后的投资组合。"
            }
        ]
    },
    {
        "name": "IndustryFinancials.get_industry_dividend_ratios",
        "description": "获取行业的股息比率数据。",
        "required_parameters": [
            {
                "name": "industry_name",
                "type": "STRING",
                "description": "行业名称。"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "industry_dividend_ratios",
                "description": "行业股息比率数据。"
            }
        ]
