    {
        "name": "EconomicIndicators.get_gdp_growth_rate",
        "description": "获取特定国家的GDP增长率。",
        "required_parameters": [
            {
                "name": "country_name",
                "type": "STRING",
                "description": "国家名称。"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "gdp_growth_rate",
                "description": "GDP增长率。"
            }
        ]
