        "name": "StockScreener.filter_stocks",
        "description": "根据特定标准筛选股票。",
        "required_parameters": [
            {
                "name": "filter_criteria",
                "type": "OBJECT",
                "description": "筛选标准。"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "filtered_stocks",
                "description": "符合条件的股票列表。"
            }
        ]
