        "name": "StockMarket.historical_data",
        "description": "获取指定股票指数的历史数据",
        "required_parameters": [
            {
                "name": "index_name",
                "type": "STRING",
                "description": "股票指数的名称"
            }
        ],
        "optional_parameters": [
            {
                "name": "start_date",
                "type": "DATE",
                "description": "数据开始日期"
            },
            {
                "name": "end_date",
                "type": "DATE",
                "description": "数据结束日期"
            }
        ],
        "return_data": [
            {
                "name": "data",
                "description": "历史数据"
            }
        ]
    },
    {
        "name": "Investment.portfolio_analysis",
        "description": "分析投资组合的表现和风险",
        "required_parameters": [
            {
                "name": "portfolio_data",
                "type": "STRING",
                "description": "投资组合的数据"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "analysis",
                "description": "投资组合的分析结果"
            }
        ]
    },
    {
        "name": "StockMarket.real_time_data",
        "description": "获取指定股票指数的实时数据",
        "required_parameters": [
            {
                "name": "index_name",
                "type": "STRING",
                "description": "股票指数的名称"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "data",
                "description": "实时数据"
            }
        ]
    },
    {
        "name": "CentralBank.monetary_policy_analysis",
        "description": "分析中央银行的货币政策",
        "required_parameters": [],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "analysis",
                "description": "货币政策分析结果"
            }
        ]
    },
    {
        "name": "Investment.recommendation",
        "description": "基于历史和实时数据生成投资建议",
        "required_parameters": [
            {
                "name": "historical_data",
                "type": "STRING",
                "description": "历史数据"
            },
            {
                "name": "real_time_data",
                "type": "STRING",
                "description": "实时数据"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "recommendation",
                "description": "投资建议"
            }
        ]
    },
    {
        "name": "Economy.gdp_forecast",
        "description": "预测国家或地区的GDP增长",
        "required_parameters": [
            {
                "name": "region",
                "type": "STRING",
                "description": "国家或地区的名称"
            }
        ],
        "optional_parameters": [
            {
                "name": "forecast_period",
                "type": "INTEGER",
                "description": "预测的时间周期（年）"
            }
        ],
        "return_data": [
            {
                "name": "forecast",
                "description": "GDP增长预测"
            }
        ]
