    {
        "name": "Currency.exchange_rate",
        "description": "获取两种货币之间的汇率",
        "required_parameters": [
            {
                "name": "base_currency",
                "type": "STRING",
                "description": "基础货币"
            },
            {
                "name": "target_currency",
                "type": "STRING",
                "description": "目标货币"
            }
        ],
        "optional_parameters": [],
