        "required_parameters": [
            {
                "name": "company_name",
                "type": "STRING",
                "description": "需要评估的保险公司名称"
            }
        ],
        "optional_parameters": [],
        "return_data": [
            {
                "name": "risk_evaluation",
                "description": "特定保险公司的市场风险评估结果"
