[['Spotify.get_user_growth_data', {'name': 'Spotify.get_user_growth_data', 'description': 'Get user growth data for Spotify over a specified time period', 'required_parameters': [{'name': 'time_period', 'description': 'time period for data retrieval', 'type': 'STRING'}], 'optional_parameters': [], 'return_data': [{'name': 'user_growth', 'description': 'user growth data over the specified period'}]}],
