['MarketAnalysis.analyze_market_changes', {'name': 'MarketAnalysis.analyze_market_changes', 'description': 'Analyze market changes for specified platforms', 'required_parameters': [{'name': 'platforms', 'description': 'list of platforms to analyze', 'type': 'LIST'}], 'optional_parameters': [], 'return_data': [{'name': 'market_changes', 'description': 'analysis of market changes for the specified platforms'}]}],
